mapper.select:
SELECT A.estate_name,COUNT(1) as survey_count,GROUP_CONCAT(C.project_name) as project_name, GROUP_CONCAT(date_format(A.gmt_created,'%Y-%m-%d') ORDER BY A.id DESC) as survey_date FROM tb_basic_apply_batch A JOIN tb_project_plan_details B ON A.plan_details_id=B.id JOIN tb_project_info C ON A.project_id=C.id WHERE A.project_id in ( SELECT D.id FROM tb_project_info D JOIN tb_project_member E ON D.id=E.project_id WHERE 1=1
AND E.user_account_manager = #{userAccount}
) AND A.bis_delete=0
AND B.project_phase_id = #{projectPhaseId}
GROUP BY A.estate_name ORDER BY A.id DESC
