mapper.select:
select distinct A.id,A.project_id,A.number_value, A.report_type,E.u_use_unit_name,D.investigations_start_date, B.project_category_id,A.area_id,B.contract_price, B.preaudit_number_date, B.result_number_date from tb_project_number_record A LEFT JOIN tb_project_info B ON A.project_id = B.id LEFT JOIN tb_declare_apply C ON A.project_id = C.project_id LEFT JOIN tb_generate_report_info D ON A.project_id = D.project_id LEFT JOIN tb_initiate_unit_information E ON A.project_id = E.project_id WHERE 1=1 and bis_delete = false
AND (A.number_value LIKE CONCAT(CONCAT('%',#{numberValue}), '%'))
AND (E.u_use_unit_name LIKE CONCAT(CONCAT('%',#{unitName}), '%'))
AND A.report_type = #{reportType}
AND (A.report_type = #{reportType} or A.report_type = #{consultationId})
AND B.preaudit_number_date >= #{previewsStartDate}
AND B.preaudit_number_date <= #{previewsEndDate}
AND B.result_number_date >= #{resultStartDate}
AND B.result_number_date <= #{resultEndDate}
